import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { fetchUser } from '../actions';

const UserHeader = ({ userId, fetchUser}) => {

    useEffect(() => {
        fetchUser(userId);
    }, [fetchUser]);

    return (
        <div>Hello there!</div>
    );
};

export default connect(null, { fetchUser })(UserHeader);
