import React from 'react';
import Modal from '../Modal';
import history from '../../history';
import { fetchStream, deleteStream } from '../../actions';
import { connect } from 'react-redux';

class StreamDelete extends React.Component {
    componentDidMount() {
        this.props.fetchStream(this.props.match.params.id);
    }

    onDeleteClick = () => {
        //this.props.deleteStream(this.props.stream.id);
    }

    renderActions = () => {
        return (
            <React.Fragment>
                <button className="ui button negative" onClick={this.onDeleteClick}>Delete</button>
                <button className="ui button" onClick={() => history.push('/')}>Cancel</button>
            </React.Fragment>
        );
    }

    renderContent = () => {
        if (this.props.stream) {
            return (
                <React.Fragment>
                    Are you sure you want to delete this stream? <br/><br/>
                    <label>Title: {this.props.stream.title}</label><br/>
                    <label>Description: {this.props.stream.description}</label>
                </React.Fragment>
            );
        }
    }

    render() {
        console.log(this.props.stream);
        return (
            <div>
                <Modal 
                    header="Delete Stream"
                    content={this.renderContent()}
                    actions={this.renderActions()}
                    onDismiss={() => history.push('/')}
                />
            </div>
        );
    }
};

const mapStateToProps = (state, ownProps) => {
    return { stream: state.streams[ownProps.match.params.id] }
};

export default connect(mapStateToProps, { fetchStream, deleteStream })(StreamDelete);
