import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_KEY = 'AIzaSyCHUCmpR7cT_yDFHC98CZJy2LTms-IwDlM';

const Convert = ({ language, text }) => {
    const [translation, setTranslation] = useState('');

    // whenever language or text changes
    useEffect(() => {
        const result = axios.post('https://translation.googleapis.com/language/translate/v2', {}, {
            params: {
                q: text,
                target: language.value,
                key: API_KEY
            }
        });

        setTranslation(result.data.translations[0].translatedText);

    }, [language, text]);

    return (
        <div>
            {language.label}
            <br />
            {text}
            < hr/>
            <h3>Translated Text: {translation}</h3>
        </div>
    );
};

export default Convert;
